styles:
body {
 background-color: #f6f6f6;
 color: #455469;
 padding: 60px;
 text-align: center 
}
 a {
 text-decoration: none;
 color: #109cf1;
}
 h1 {
 font-size: 40px;
 text-align: center;
}
 h2 {
 font-size: 36px;
}
 h3 {
 font-size: 32px;
}
 h4 {
 font-size: 28px;
}
 h5 {
 font-size: 24px;
}
 h6 {
 font-size: 20px;
}
 .content {
 background-color: #fff;
 border-radius: 4px;
 margin: 0 auto;
 padding: 48px;
 width: 600px 
}
 .button {
 background-color: #109cf1;
 border: none;
 color: white;
 padding: 15px 32px;
 text-align: center;
 text-decoration: none;
 display: inline-block;
 font-size: 16px;
}
